(63, 'Add a new user on linux/unix command line', '', '<div style="font-family: gotham, helvetica, arial, sans-serif;"><p>Add User:<br clear="none">#Add user and set password<br clear="none">useradd username<br clear="none">passwd username (this will then prompt for a password, issue the password, then retype)</p><p>#so they can not ssh to server</p></div><div style="font-family:"Helvetica Neue", Arial, sans;font-size:16px;">usermod -s /sbin/nologin username</div><div style="font-family:"Helvetica Neue", Arial, sans;font-size:16px;"><br></div><div style="font-family:"Helvetica Neue", Arial, sans;font-size:16px;">#Test if the user was added</div><div style="font-family:"Helvetica Neue", Arial, sans;font-size:16px;"><br></div><div style="font-family: gotham, helvetica, arial, sans-serif;">cat /etc/passwd | grep username<p><br></p><p>#Edit user directory</p><div>vim /etc/passwd</div></div>', 'add user, linux permissions, create user linux', 0, '', 1, 1, '2016-12-05 11:26:27', '2016-12-06 12:54:55', 6, '1', '0000-00-00', NULL, 0),
(64, 'Forward a page with query string in it using htaccess', '', '<div style="font-family: gotham, helvetica, arial, sans-serif;"><p>forward a page with query string in it</p><p>for example :</p><div><span style="letter-spacing: 0.025em;">domainname.com/?somepage=10 >>>Â </span><span style="letter-spacing: 0.025em;">domainname.com/someotherpage</span></div><div><br clear="none"></div></div><div style="font-family:"Helvetica Neue", Arial, sans;font-size:16px;"><br clear="none"></div><div style="font-family:"Helvetica Neue", Arial, sans;font-size:16px;">RewriteCond %{REQUEST_URI} ^/$</div><div style="font-family: gotham, helvetica, arial, sans-serif;"><div><br clear="none"></div><p>RewriteCond %{QUERY_STRING} ^somepage=10$</p><div><br clear="none"></div><p>RewriteRule ^(.*)$ /someotherpage/ [R=301,L]</p><div><br clear="none"></div><p>//rest of the htaccess rules.</p></div>', 'htaccess', 0, '', 1, 1, '2016-12-05 11:27:24', '2016-12-06 12:19:09', 6, '1', '0000-00-00', NULL, 0),
